import random#function which creates a random listdef random_list_generator(length, range1, range2):    arbitrary_list = []    for i in range(length):        arbitrary_list.append(random.randrange(range1, range2))    return arbitrary_list#sorting function (bubble sort)def sorting(lst):    n = len(lst)    for i in range(n):        for j in range(n - i - 1):            if lst[j] > lst[j + 1]:                lst[j], lst[j + 1] = lst[j + 1], lst[j]    return lst                    #generating a listtest_list =random_list_generator(10, -15, 15)print('randomly generated list: ', test_list)#sorting itprint('after sorting it: ', sorting(test_list))